import axios from "axios";

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:8000",
  timeout: 5000,
});

// helpers to map backend <-> frontend field names
function mapOut(item) {
  return {
    id: item.id,
    email: item.email,
    type: item.type,
    location: item.location,
    // map DB status to frontend expected status names
    status:
      item.status === "Open"
        ? "active"
        : item.status === "Assigned"
        ? "maintenance"
        : item.status === "Closed"
        ? "inactive"
        : item.status,
    openDate: item.open_date || item.openDate,
  };
}

function mapIn(payload) {
  // backend expects snake_case open_date is handled server-side
  return {
    email: payload.email,
    type: payload.type,
    location: payload.location,
    status:
      payload.status === "active"
        ? "Open"
        : payload.status === "maintenance"
        ? "Assigned"
        : payload.status === "inactive"
        ? "Closed"
        : payload.status,
  };
}

export async function listAssets(status) {
  const params = {};
  if (status) {
    params.status = status;
  }
  const res = await api.get("/assets", { params });
  // support APIs that return either an array or an object with a `value` array
  const payload = Array.isArray(res.data) ? res.data : res.data?.value || res.data?.items || [];
  return payload.map(mapOut);
}

export async function createAsset(asset) {
  const res = await api.post("/assets", mapIn(asset));
  return mapOut(res.data);
}

export async function updateAsset(id, patch) {
  const res = await api.put(`/assets/${id}`, mapIn(patch));
  return mapOut(res.data);
}

export async function deleteAsset(id) {
  await api.delete(`/assets/${id}`);
  return true;
}

export default { listAssets, createAsset, updateAsset, deleteAsset };
